                    inputClassName="input-style"
                    idDatalist="id-data-list"
                    listData={['o','0']}
                    value={JSON.parse(predictNow)[2]}
                />
                <BtnRender
                    btnName="Sync Data"
                    idBtn="syncDataStream"
                    classBtn="btn"
                    btnClick={HandleSyncDataStream}
                />
            </div>
        )
